    private void jButton23ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton23ActionPerformed
        String valorTxtResultado = txtResultado.getText();
        if (valorTxtResultado.length() > 0) {
            valorTxtResultado = valorTxtResultado.substring(0, valorTxtResultado.length() - 1);
            txtResultado.setText(valorTxtResultado);
         
            if (valorTxtResultado.length() == 0) {
                txtResultado.setText("0");
            }
        } else {
            txtResultado.setText("0");
        }

    }//GEN-LAST:event_jButton23ActionPerformed

    private void jButton25ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton25ActionPerformed
        pegarNum1();
        sinal = '!';
        double fatorial = 1, i;
        for (i = valor1; i >= 1; i--) {
            fatorial = fatorial * i;
        }
        txtResultado.setText(String.valueOf(fatorial));
        setarValoresNoLbTemp();
    }//GEN-LAST:event_jButton25ActionPerformed

    private void jButton26ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton26ActionPerformed
        pegarNum2();
        if ('0' != sinal)
            switch (sinal) {
                case '*':
                    txtResultado.setText(String.valueOf(valor1 * (valor2 / 100)));
                    break;
                default:
                    break;
            }
        else
            txtResultado.setText("0");
        setarValoresNoLbTemp();
    }//GEN-LAST:event_jButton26ActionPerformed

    private void jButton27ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton27ActionPerformed
        pegarNum1();
        sinal = '√';
        txtResultado.setText(String.valueOf(Math.sqrt(valor1)));
        setarValoresNoLbTemp();
    }//GEN-LAST:event_jButton27ActionPerformed

