    private void formKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_formKeyPressed
        if (evt.getKeyChar()=='0'
            || evt.getKeyChar()=='1'
            || evt.getKeyChar()=='2'
            || evt.getKeyChar()=='3'
            || evt.getKeyChar()=='4'
            || evt.getKeyChar()=='5'
            || evt.getKeyChar()=='6'
            || evt.getKeyChar()=='7'
            || evt.getKeyChar()=='8'
            || evt.getKeyChar()=='9') {
            setarNumerosNoLbTela(evt.getKeyChar());
        }
        System.out.println(evt.getKeyChar());
    }//GEN-LAST:event_formKeyPressed

