    public void setarNumerosNoLbTela(char numero) {
        if ("0".equals(txtResultado.getText()) && !".".equals(String.valueOf(numero))) {
            txtResultado.setText("");
        }

        String texto = txtResultado.getText();
        texto = texto + numero;
        txtResultado.setText(texto);
    }
    
    public void pegarNum1(){
        valor1 = Double.parseDouble(txtResultado.getText());
        txtResultado.setText("0");
    }
    
    public void pegarNum2(){
        valor2 = Double.parseDouble(txtResultado.getText());
        txtResultado.setText("0");
    }
    
    public void setarValoresNoLbTemp(){
        lbTemp.setText(valor1+""+sinal);
    }
    
    public void reiniciarCalculadora(){
        valor1 = null;
        valor2 = null;
        txtResultado.setText("0");
        lbTemp.setText("");
    }
